[Relation 클래스]
public class Relation implements Information {
private int[][] blockState;  // 봉쇄여부
private int n; // 지역수
private Local[] local;
private double[][] spreadPercent;  // 전파확률
public Relation(Local[] local) {  // 지역 인스턴스를 받으며 초기값을 설정
n = local.length;
blockState = new int[n][n];
spreadPercent = new double[n][n];

for(int i=0; i<n; i++) {
for(int j=0; j<n; j++) {
blockState[i][j] = 1;  // 초기상태는 지역간 개방상태
spreadPercent[i][j] = Math.random() * 0.3;  // 전파확률은 0~30%
}
}
this.local = local;
}

public boolean getSpread(int i) {  // 전파여부 리턴하는 메서드
boolean flag = false;

for(int j=0; j<n; j++) {  // 두 지역간에 개방상태이고 전파하고자 하는 지역이 감염되었을 경우만 확률계산
double r = Math.random();
flag = blockState[i][j] * blockState[j][i] * (local[j].getInfectionState() ? 1 : 0) * spreadPercent[j][i] > r;
if(flag) { break; }
}
return flag;  // true면 감염
}
public void setBlockState(int i) {  // A지역이 나머지 지역 봉쇄
// 아직 구현못함 (예외처리 들어감)
}
public void setBlockState() {  // 나머지 지역이 다른 지역 봉쇄
for(int i=1; i<n; i++) {
for(int j=0; j<n; j++) {
if(i != j) { 
blockState[i][j] = (local[i].getActivity() >= 0.25 * local[j].getPatients() - 50) ? 1 : 0;
}  // 활성도 값이 100이면 600명 이상인 지역만 봉쇄, 활성도가 0이면 200명 이상인 지역부터 봉쇄
}
}
}
public int[][] getBlockState() { return blockState; }   // 봉쇄여부 리턴
}  
