public abstract class Government {
   private final double defaultDecrease = 0.97;
   protected double changes; // int형 변수(예산, 민심), double형 변수(감염률)을 모두 받기 위함
   protected Information relation;  // 관계클래스의 인스턴스
   protected Local local;  // 지역클래스의 인스턴스
      public void setGovernment(int j) {
      switch(j) {
      case 0: changes = local.getBudget();  // 봉쇄, 경제활성, 백신개발에 따른 예산변화
      case 1: changes = local.getSentiment();  // 민심변화
      case 2: changes = local.getInfectionRate();  // 감염률변화
      }
   }
   public void setGovernment(int i, Information relation, Local[] local) {
      this.relation = relation;  // Covid 클래스의 relation과 local인스턴스와 동일한 객체로 만듬
      this.local = local[i];   
   }
   public abstract void Block(int i);  // 봉쇄정책 수행
   public abstract void EconomyActivity(double activity);  // 경제활성 수행
   public abstract void VaccineDevelopment(boolean vaccine);  // 백신개발 수행
   public double getChanges() { return changes; }  // double로 리턴해줘 필요한 부분만 int로 형변환시킴
}
[Budget 클래스]
public class Budget extends Government {
   private int[][] blockState;

   public void Block(int i) {  // 봉쇄지역 하나당 예산 5 감소
      blockState = relation.getBlockState();  // 봉쇄여부
      int n = blockState.length;  // 지역 수
      int sum = 0;  // 개방된 지역 수
      for(int j=0; j<n; j++) {
         sum += blockState[i][j] * blockState[j][i];
      }
      int blockLocal = n - sum;  // 봉쇄된 지역 수
      changes -= blockLocal * 5.0;  // 자료형을 double로 변환
   }

   public void EconomyActivity(double activity) {  // 경제활성 정책
      double tendency = 1.0 + 0.4 * (activity / 100);  // 경향 값은 경제활성도가 100이면 1.4, 0이면 1.0
      double r = Math.random() * 200 - 100;
      double activityRate = tendency + 1 / r;  // 경제활성율은 경향 값에서 약간의 랜덤값만큼 차이남
      if(activityRate < 1.0) { activityRate = 1.0; }  // 경제활성율은 1.0에서 1.2 사이의 값
      if(activityRate > 1.4) { activityRate = 1.4; }  // 예산은 0~40%만큼 증가할 수 있음
      changes *= activityRate;
   }

   public void VaccineDevelopment(boolean vaccine) {  // 백신 개발 시 예산 25 감소
      changes -= vaccine ? 25 : 0;
   }
}
